import React, { useState } from "react";
import UserCard from "../components/UserCard/UserCard";
import UserList from "../components/UserList/UserList";
import getUserData from "../misc/configData";
const getApiData = async()=>{
  const data = await getUserData().then((result)=>result.data);
  return data;
}
function UserComponentWrapper() {
  const [user, setUser] = useState(null);
  const userData = getApiData;
  console.log("inside component ", userData);
  return (
    <div className="bg-white  ms-5 me-5">
      <UserList
        userData={userData}
        handleHover={(user) => {
          setUser(user);
        }}
      />
      <UserCard user={user} />
    </div>
  );
}

export default UserComponentWrapper;
