const initialData = {
  user: { image: "", name: "", email: "", isVisible: false },
};
export const setUser = (state = initialData, action) => {
  const { image, name, email } = action.payload;
  switch (action.type) {
    case "mouseEnter":
      return {
        user: {
          image: image,
          name: name,
          email: email,
          isVisible: true,
        },
      };
    case "mouseLeave":
      return {
        initialData,
      };
  }
};
