import React, { useState } from "react";
import UserCard from "../components/UserCard/UserCard";
import UserList from "../components/UserList/UserList";
// import userData from "../misc/configData";

function UserComponentWrapper() {
  const [user, setUser] = useState(null);
  const getUserData = async () => {
    const response = await fetch("https://reqres.in/api/users?page=1")
      .then((r) => r.json())
      .then((result) => result.data);
    console.log(response);
    return response;
  };
  const userData = getUserData();
  // const userData = getUserData().then((result) => result.data);
  // getUserData().then((result) => setUserData(result.data));

  console.log("inside component ", userData);
  return (
    <div className="bg-white ms-5 me-5">
      <UserList
        userData={userData}
        handleHover={(user) => {
          setUser(user);
        }}
      />
      <UserCard user={user} />
    </div>
  );
}

export default UserComponentWrapper;
